package tasks.leetcode;

import java.util.*;

public class FindAllAnagramsInAString {

    public static void main(String[] args) {
        String s = "";
        String p = "";

        findAnagrams(s, p);
    }

    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (p.length() > s.length()) {
            return result;
        }
        if ("".equals(p) || "".equals(s)) {
            return result;
        }
        Map<Character, Integer> pMap = new HashMap<>();
        Map<Character, Integer> sMap = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            pMap.merge(p.charAt(i), 1, (val, acc) -> val + acc);
            sMap.merge(s.charAt(i), 1, (val, acc) -> val + acc);
        }


        if (equals(sMap, pMap)) {
            result.add(0);
        }

        for (int i = p.length(); i < s.length(); i++) {
            sMap.compute(s.charAt(i - p.length()), (k, v) -> v == 1 ? null : v - 1);
            sMap.merge(s.charAt(i), 1, (val, acc) -> val + acc);
            if (equals(sMap, pMap)) {
                result.add(i - p.length() + 1);
            }
        }

        return result;
    }

    private static boolean equals(Map<Character, Integer> map1, Map<Character, Integer> map2) {
        if (map1.size() != map2.size()) return false;
        for (char c : map1.keySet()) {
            if (map2.get(c) == null || map1.get(c) != map2.get(c)) {
                return false;
            }
        }
        return true;
    }

}
